mapper.select:
select DISTINCT s.*,t.* from ( select ab.*,geo.id orderNumber from ( select a.hall_name hall_name ,b.id,b.table_codee,b.table_name,b.service_charge,b.minimum_consumption,b.status from (select * from gs_hall where 1=1
and id=#{hallId}
and hall_name like '%${hallName}%'
)a left join ( select b.id,b.table_codee,b.table_name,b.service_charge,b.minimum_consumption,b.status ,b.HALL_ID from gs_diningtable b where b.status!=0
and b.status=#{state}
) b on a.id=b.hall_id)ab left join GS_REPAST_ORDER geo on ab.id=geo.ctid )s left join ( select a.id reserveId,a.customer_name,a.people_number,b.table_id,a.contact_number,b.reserve_start_time,b.reserve_end_time from gs_restaurant_reservation a ,reserve_halls b where a.id=b.reserve_id and a.state='1' and b.reserve_hall_status='1' and ((UNIX_TIMESTAMP(b.reserve_start_time)-UNIX_TIMESTAMP(now()))/3600 between 0 and 5) )t on s.id=t.table_id
