mapper.select:
select a.*,b.* from ( select b.buildingname,c.floorname,d.typeid,d.typename,d.houseprices,a.id,a.roomno,a.stdpax,a.maxpax,a.doorlockid,a.flag,a.sflag,a.cflag from gs_room a ,gs_building b,gs_floor c,gs_roomtype d where a.buildingcode=b.buildid and a.floorcode =c.FLOORID and a.roomtype=d.typeid
and a.floorcode in (${floorId})
and a.roomtype=#{typeId}
) a left join ( select a.bookingno,a.guestname,a.startdate,a.enddate,a.teamname,a.remark,b.roomid ,a.bookingflag from gs_quickbooking a,gs_quickbooking_roomno b where a.id=b.bookid and (a.bookingflag='明确预定' or a.bookingflag='预订入住') )b on a.id =b.roomid

select a.id,a.roomid,a.roomno,a.doorLockId,case when b.price is null then a.housePrices else b.price end housePrices ,a.typename,a.typeid from ( SELECT a.id,a.roomid,a.roomno,a.doorLockId,b.housePrices,b.typename,b.typeid FROM gs_room a,gs_roomtype b where a.ROOMTYPE=b.typeid AND a.FLAG='空房' AND b.typeId IN
#{str}
) a left join REALTIMEHOUSEPRICE b on a.typeid=b.typeid and b.time=date_format(now(),'%Y-%m-%d')

select * from
(
SELECT c.buildingname,d.floorname,b.typename,a.id,a.roomno,a.remark,a.flag,a.sflag from gs_room a,gs_roomtype
b,gs_building c ,gs_floor d
WHERE a.roomtype=b.roomtype AND a.buildingcode = c.buildingcode AND a.floorcode = d.floorcode AND

    AND a.roomType = #{roomType}


    AND a.buildingCode=#{buildingCode}


    AND a.floorCode = #{floorCode}


    and a.flag= #{flag}


    and a.sflag= #{sflag}

) x
LEFT JOIN
(
select
w.roomid,q.bookingno,q.guestname,q.markets,q.salesman,q.startdate,q.enddate,q.releasetime,q.days,q.remark,w.roomno
from gs_quickbooking q ,gs_quickbooking_roomno w where q.id = w.bookid

    AND unix_timestamp(q.startdate) >= unix_timestamp(#{endDate})


    and unix_timestamp(q.enddate) <= unix_timestamp(#{endDate})

) y
ON x.roomno = y.roomid

select a.typeid,b.typename,a.time,a.price from REALTIMEHOUSEPRICE a,GS_ROOMTYPE b where a.typeid=b.typeId and
time >=DATE_FORMAT(NOW(),'%Y-%m-%d')

    and subString(time,1,7)=#{time}
